import React, { useState,useEffect } from 'react'
import axios from 'axios';
import './Card.css' 

const Card = (props) => {
    console.log(props.data);
    
    const [FromStation, setFromStation]=useState('');
    const [ToStation, setToStation]=useState('');
    const [TrainNo, setTrainNo]=useState('');
    const [FareData , setFareData] = useState([]); 
    const options = {
        method: 'GET',
        url: 'https://irctc1.p.rapidapi.com/api/v2/getFare',
        params: {
          trainNo: FromStation,
          fromStationCode: ToStation,
          toStationCode: TrainNo
        },
        headers: {
          'X-RapidAPI-Key': '018e22e869msha6b3de57513e85bp19e748jsncf4a2cc3620d',
          'X-RapidAPI-Host': 'irctc1.p.rapidapi.com'
        }
      };


    const handleBooking =async ()=>{
        setFromStation(props.data.from_station_name);
        setToStation(props.data.to_station_name);
        setTrainNo(props.data.train_number);

        try {
            const response = await axios.request(options);
            console.log(response.data);
            setFareData(response.data.data);

        } catch (error) {
            console.error(error);
        }
    }


    useEffect(()=>{

    },[FareData])

    
  return (
    <div id="details">
        <div id='stationdetails'>
            <div id='fromsection'>
                <h3>From : {props.data.from} </h3>
                <h3>Station Name : {props.data.from_station_name} </h3>
                <p>Time : {props.data.from_sta} </p>
            </div>
            <div id='timedetails'>
                <h3>Train Name : {props.data.train_name} </h3>
                <p>Train Class : {props.data.class_type} </p>
                Train Number : {props.data.train_number} 
                <p>Runs On : </p>
                <p>Date : {props.data.train_date}</p>
                <p>Duration : {props.data.duration}</p>

            </div>
            <div id='tosection'>
                <h3>To : {props.data.to} </h3>
                <h3>Station Name : {props.data.to_station_name} </h3>
                <p>Time : {props.data.to_sta} </p>
            </div>
        </div>
        <div id='train_book'>
            <button className='train_button' name='add_booklist'> Add to list</button>
            <button className='train_button' name='book_ticket' onClick={handleBooking} > Book </button>
        </div>
    </div>
  )
}

export default Card
