import React, { useState } from 'react'
import Navbar from './Navbar';
import './Navbar_style.css';
import './AccountPage.css';
import axios from 'axios';
import Cookies from 'js-cookie';

export const AccountPage = () => {
	// const userEmail = getCookieValue('email'); // Replace with your cookie reading logic
	const userEmail = Cookies.get('email'); // Replace with your cookie reading logic
	const [userdetails , setdata] = useState([]);
	axios.get(`http://localhost:3001/user/${userEmail}`)
		.then(response => {
			setdata(response.data);
			console.log(response.data); // User data
		})
		.catch(error => {
			console.error('Error fetching user data:', error);
		});
	return (
		<div>

			<Navbar />
			<div className="account-container">
				<div className="photo-section">
					<img src="www.google.com" alt="Profile" />
				</div>
				<div className="info-section">
					<h2>Name : ${userdetails.FirstName} </h2>
					<p><strong>Email: ${userdetails.EmialId}</strong></p>
					<p><strong>Address: ${}</strong> </p>
				</div>
			</div>
		</div>
	)
}


export default AccountPage;
